            NavigationBar {
                DashboardTab.entries.forEach { tab ->
                    NavigationBarItem(
                        selected = selectedTab == tab,
                        onClick = {
                            selectedTab = tab
                            when (tab) {
                                DashboardTab.Submit -> onNavigateSubmit()
                                DashboardTab.History -> onNavigateHistory()
                                DashboardTab.Settings -> onNavigateSettings()
